$ipAddress = (Get-NetIPAddress | Where-Object {$_.AddressState -eq "Preferred" -and $_.ValidLifetime -lt "24:00:00"}).IPAddress; $useruser = $env:UserName; $hostname = hostname; $total = "Username: " + $useruser + " Computer: " + $hostname + " IPv4: " + $ipAddress; Set-Clipboard -Value $total; Start-Process https://docs.google.com/document/d/13Ct19ZLc2Lah5LJRwLtXRSjs96bFj-k2iFz29PJ6Zxg/edit; exit
